css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http), url-prefix(file) {
button,
input[type="reset"],
input[type="button"],
input[type="submit"],
input[type="file"] > input[type="button"], input[type="radio"] { background-color: #FFFFFF; color: #000000; }

textarea, button, select, input { background-color: #FFFFFF; color: #000000; }
}
