css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("typo3.org") {
  p { margin: 5px 0; font-size: 11px; line-height: 15px; }
  #main-column ol li {
    margin: 0px 0pt 0px 30px;
  }
  .tx-terdoc-pi1 h2 {
    font-size: 18px;
    margin: 18px 0 5px 0;
  }
  #rootline-nav { margin:0pt 12px 5px 0pt; }
  #left-column { width: 170px; }
  div#sponsors { margin:0pt 0pt 0pt 10px; }
  #typo3org-container { width:820px; }
  #typo3org-container #main-column { width:600px; }
  #username { margin:20px 5px 5px 5px; width:90px; }
  #logo { height: 55px; }
  #logo img { margin:5px 0pt 0pt 20px; }
  #account-login {margin: 5px 0 0; }
  #services { margin: 10px 12px 0 0; width: 470px; }
  #services form { float:left; margin:0pt 5px; }
}
