css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pixiv.net") {
  .adver_top,
  .adver_topBottom,
  .adver_footer,
  .adver_footerBottom,
  .adver_Rightcolumn,
  .adver_subframe,
  .ads_area,
  .rankingAdver,
  .rankingAdver02,
  .rankingAdver03 {
    display: none;
  }

  .rankingCenter {
    float: none !important;
    margin: 0 auto !important;
  }
}

@-moz-document domain("dic.pixiv.net") {
  #header > a:first-child + p {
    display: none;
  }
 
  .overture_ads_top,
  .overture_ads_search,
  .overture_ads_articl {
    display: none;
  }
}
