css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {
div {
border:none !important;
}
td.reply {
-moz-border-radius:5px;
border: 1px solid rgba(128,0,0,.1) !important;
border-width:0px 0px 1px 1px !important;
background-image: -moz-linear-gradient(45deg,rgba(128,0,0,.1), rgba(128,0,0,0) 100px) !important;
}
span{
background:transparent !important;
}
td.postblock, input, td.replyhl{
-moz-border-radius:5px;
border: 1px solid rgba(128,0,0,.5) !important;
border-width:0px 0px 1px 1px !important;
background-image: -moz-linear-gradient(45deg,rgba(128,0,0,.3), rgba(128,0,0,0) 100%) !important;

}
hr {
display:none;
}
form div, center, #head {
background-image: -moz-linear-gradient(90deg,rgba(240,224,214,0),rgba(240,224,214,.5) 1px, rgba(240,224,214,0) 100%) !important;
margin-bottom:20px;
}
div form input div { /*why the fuck do i have to do div???*/
background-image: -moz-linear-gradient(90deg,rgba(240,224,214,0),rgba(240,224,214,.1) 50%, rgba(240,224,214,0) 100%) !important;
}
div form textarea div { /*why the fuck do i have to do div???*/
background: -moz-radial-gradient(center 45deg, circle farthest-side, rgba(240,224,214,.2) 0%, rgba(240,224,214,0) 100%) !important;
}

}
