@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opengameart.org") {

/* this hides stuff */
#right, #footer, #admin-menu, #top {
  display: none !important;
}

/* this makes stuff wide*/
#center, #mainbar, #content {
  width: 100% !important;
}

/* this removes decoration and does the colors */
body, div, p, pre, textarea, ul, li, ol, a, span, table, thead, td, tr, th, input, h1, h2, h3, h4, h5 {
  /*background-color: #555555 !important;
  color: #ddd !important;
  background-image: none !important;*/
  filter:alpha(opacity=100) !important;
  -moz-opacity: 1 !important;
  opacity: 1 !important;
  -ms-filter:'alpha(opacity=100)' !important;
  border: none !important;
  -moz-box-shadow: none !important;
  -moz-border-radius: 0 !important;
}
h1, h2, h3, h4, h5 {
  /*background-color: #444444 !important;*/
  padding: .25em !important;
}
a {
  /*background-color: #333333 !important;*/
  padding: 2px !important;
  background: none !important;
}
.field-content div, .content div {
  /*background-color: #444444 !important;*/
}
body, .sf-menu * {
  background-color: #ffffff !important;
}
.sf-menu * {
  color: blue !important;
}
}
