css:
#appmenu-button {
  padding: 0 18px 1px 18px !important;
  -moz-box-shadow: 0 0 2px 0 rgba(255,255,255,.75) inset,
                   0 1px 0 rgba(255,255,255,.45) inset !important;
  border: 2px solid !important;
  border-top: 0!important;
  background-color: rgba(255,255,255,1) !important;
}

#main-window:not([privatebrowsingmode]) #appmenu-button {
  -moz-border-left-colors: rgba(255,255,255,.5) rgba(83,42,6,.9) !important;
  -moz-border-bottom-colors: rgba(255,255,255,.5) rgba(83,42,6,.9) !important;
  -moz-border-right-colors: rgba(255,255,255,.5) rgba(83,42,6,.9) !important;
  background-image: -moz-linear-gradient(rgba(244,172,17,.85),
                                         rgba(218,107,17,1) 100%) !important;
}

#main-window:not([privatebrowsingmode]) #appmenu-button:hover:not(:active):not([open]) {
  background-image: -moz-linear-gradient(rgba(244,172,17,.70),
                                         rgba(218,107,17,1) 100%) !important;
}

#main-window[privatebrowsingmode="temporary"] #appmenu-button {
  -moz-border-left-colors: rgba(255,255,255,.5) rgb(82,19,140) !important;
  -moz-border-bottom-colors: rgba(255,255,255,.5) rgb(82,19,140) !important;
  -moz-border-right-colors: rgba(255,255,255,.5) rgb(82,19,140) !important;
  background-image: -moz-linear-gradient(rgba(153,38,211,.85),
                                         rgb(105,19,163) 100%) !important;
}

#main-window[privatebrowsingmode="temporary"] #appmenu-button:hover:not(:active):not([open]) {
  background-image: -moz-linear-gradient(rgba(153,38,211,.70),
                                         rgb(105,19,163) 100%) !important;
}

#appmenu-button:hover:active,
#appmenu-button[open] {
  -moz-box-shadow: inset 0 0 2px 0 rgba(0,0,0,.4) !important;
}
