css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.discontinuity.info/~pkhuong/common-cold/") {
  body { font-size: 1.2em !important; }
  body, body > div { background: #000 !important; color: #FFF !important; font-family: Cambria, serif !important; width: auto !important; }
  body > div { margin: 0 auto !important; max-width: 45em !important; }
  body > div p { max-width: 42em !important; width: auto !important; text-indent: 1em !important; margin: 0 auto !important; }
  body > div * + p { margin-top: 0.2em !important; }
  body > div p + p { margin-top: 0.4em !important; }
  body > div li p  { text-indent: 0 !important; }
  body > div :link, body > div :visited { text-decoration: none !important; border-bottom: 1px dashed !important; }
  body > div :hover { border-bottom: 1px solid !important; }
  body > div :link { color: #F00 !important; }
  body > div :visited { color: #900 !important; }
}
