css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("insanejournal.com") {
body.sitescheme,
.sitescheme table {
  background: #AC5F61 !important;
  color: #D4DFC3 !important;
  font: 12px Verdana, Tahoma, Geneva, sans-serif !important;
}

.sitescheme a, 
.sitescheme a:visited,
.sitescheme a:active {
  color: #98AC82 !important;
  text-decoration: none !important;
}

.sitescheme a:hover {
  text-decoration: underline !important;
}

.sitescheme table.nav {
  border: dashed 2px #913D40 !important;
}

.sitescheme a.nav:visited {
  color: #805653 !important;
}
    
.sitescheme a.nav:hover {
  color: #4d7086 !important;
}  
 
.sitescheme td.head {
        color: #913D40 !important;
        background-color: #913D40 !important;
        font: 10px Verdana, Tahoma, Geneva, Arial, sans-serif !important;
  }  

.sitescheme table.banner {margin-left:-520px !important;}
}
