css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://userscripts.org/scripts/review/) {

#container {
width:99% !important;
}
.notice, .error{
display:none !important
}
#content {
border:none !important;
width:100% !important;
padding:0 !important;
position:relative !important;
top:60px !important;
}
#right {
position:absolute !important;
top:50px !important;
width:99% !important;
height:58px !important;
margin:0 !important;
border-bottom:#EEEEEE 5px solid !important;
}
#right>*{
width:175px !important;
margin:0 !important;
position:absolute !important;
}
#right>form {
left:750px !important;
padding:0 !important;
top:-4px !important;
}
#right>p {
left:20px !important;
}
#right>p+p {
left:160px !important;
top:5px !important;
}
#right>ul {
left:300px !important;
top:5px !important;
}
#right>h6 {
left:450px !important;
border:none !important;
}
#right>h6:after{
content:":" !important;
}
#right>div {
left:540px !important;
top:5px !important;
}
#right>p.flag {
left:730px !important;
padding:0 !important;
top:34px !important;
}
#footer {
clear:both !important;
position:relative !important; 
margin-top:60px !important;}
}
