css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(opensubtitles.org){

.a_1, .a_2,
#network,
.logo,
.msg.star,
.msg.hint,
.bar > ul > :first-child,
.bar > ul > :last-child,
.content > div:nth-child(9),
.content > div:nth-child(12),
.content > div:nth-child(13),
.content > fieldset:not([style]):not([class]),
.content > p,
.content > h1,
.content > h2,
#alphabet,
#lang_footer
{
  display: none !important;
}

.content {
  position: relative !important;
}

.content > :nth-child(5) {
  top: 30px !important;
  right: 5px !important;
}

.top_info, .top_info_left, .top_info_right, #menu > ul {
  width: auto !important;
}

.top_info, .top_info_right {
  border: none !important;
}

.bar {
  float: left;
  clear: none !important;
}

.header {
  height: auto !important;
  float: right;
}


}
