css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("imo.im") {
body {
background: url(http://img248.imageshack.us/img248/4182/aurorabypkarwowski.jpg);
background-size: 100%;
}
.toolbar {
background: black !important;
}
.toolbar_item {
color: #fff !important;
}
.imofooter {
display: none;
}
.accountsConsole {
background: black !important;
color: white;
-moz-border-radius: 15px;
border: none !important;
opacity: 0.8;
}
.buddy > .status {
display: none !important;
}
.accountsConsole:not(:hover) table,
.accountsConsole:not(:hover) .moreAccounts,
.accountsConsole:not(:hover) .linker > span,
.accountsConsole:not(:hover) img {
display: none !important;
}
}
