css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

#pagetop,
#watch-headline-container,
#watch-video-container,
#watch-sidebar,
#downloadfromyoutube,
#comments-post,
#comments-view > div.comments-section > h4,
#pagebottom,
#watch-panel>div:not([id="watch-discussion"]) {
    display : none !important;
}

#watch-panel,
#watch-discussion,
#comments-view .comments-section {
    padding: 0 !important;
    margin: 0 !important;
}

.comment-list .comment {
    padding: 3px 3px 8px !important;
}

#content * {
    font-size: 11px !important;
}

}
