css:
@-moz-document domain('xenforo.com') {
html {
background-color: #333;
}

/* floating header bar...
header {
position: fixed;
top:0;
left:0;
right:0;
z-index: 52;
}
#loginBar {
z-index: 9999;
}
end floating header bar */

/* Floating Whats New Button */
a[href~="find-new/threads"] {
 position: fixed; bottom: 0; right: 0; width: 60px; height: 20px; background-color: #176093;
}
/* Floating Whats New Button */

/* user ignores */
li.message[data-author~="erich37"],li[data-author~="vrtsolus"] {
 height: 10px;
 text-indent: -9999px !important;
 overflow: hidden;
 padding: 0px !important;
}
li.message[data-author~="erich37"]:hover,li[data-author~="vrtsolus"]:hover {
 height: auto;
 overflow: auto;
 background: #FFFF99;
 text-indent: 5px !important;
}
/* end user ignores */
}
