css:
/*
* Focus rectangle
*  http://userstyles.org/styles/35264
* Created by AndreasSE
*  http://userstyles.org/users/58590
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http:"), url-prefix("https:"), url-prefix("ftp:"), url-prefix("file:") {
    input:not([type=image]):hover, form input[type=image]:hover, textarea:hover, select:hover, button:hover, *[contentEditable]:not([contentEditable=false]):hover {
        outline: solid 1px goldenrod;
        -moz-outline-radius: 2px;
    }
    input:not([type=image]):focus, form input[type=image]:focus, textarea:focus, select:focus, button:focus, *[contentEditable]:not([contentEditable=false]):focus {
        outline: solid 1px dodgerblue;
        -moz-outline-radius: 2px
    }
    label:hover {
        outline: dotted 1px goldenrod;
    }
}
