css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("learnyousomeerlang.com") {

  body, div#grass{
    background:black !important;
  }
  div#wrapper {
    max-width:98% !important;
  }
  div#content div.note p, div.note {
    background: #444 !important;
    color:#FFFFFF !important;
  }
  div#content code, div#content var {
    background:none repeat scroll 0 0 #013 !important;
    color:#fff !important;
  }
  div#content td, div#content p {
    color:#ccc !important;
  }
  div.syntaxhighlighter div.lines {
    background:none repeat scroll 0 0 black !important;
  }
  div.syntaxhighlighter .line.alt2 .content, div.syntaxhighlighter .line.alt3 .content {
    background-color:#333 !important;
  }
  div.syntaxhighlighter .line.alt1 .content {
    background-color:#333 !important;
  }
  div#content blockquote pre {
    color:#BBBBBB !important;
  }
  div#content .syntaxhighlighter code, div#content blockquote {
    background:none repeat scroll 0 0 #333 !important;
  }
  p{font-size:105% !important;}
  div#header {height:0 !important;}
  div#content img{margin: 5px !important;}
}
