css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.fefe.de") {

body {

font-family:  Verdana, Geneva, Tahoma, sans-serif;
font-size: 10pt;
padding: 0px;
margin: 0px;

}

h2 {

margin-left: 8pt;

}

h2 + b {

margin-left: 8pt;

}

p[align=right] {
margin-right: 8pt;
}

div[align=right] {
margin: 8pt;
}


ul {
list-style-type: none;
margin: 0px;
padding: 0px;
}

ul li {

border : 0px solid black;
padding : 3pt;
margin: 3pt;
margin-right: 8pt;
margin-left: 8pt;
background-color: #EDEDED;

}


ul li:hover {
background-color: #D4D4D4;


}


h3 {

text-align: right;
color: white;
font-size: 13pt;


background-color: grey;

padding : 3pt;
padding-right: 8pt;

margin-top: 8pt; 
margin-bottom: 8pt;
}

blockquote {

border-left: 3px solid grey;
padding-left: 5pt;
font-style: italic;

}


}
