.stuffbox br{display:none!important}
.stuffbox img{height:15px!important;margin-right:.25em!important;width:auto!important}
.stuffbox .item{border-bottom:dotted 1px #c0c0c0}
.stuffbox span{margin-right:.25em!important}
.stuffbox table{border-collapse:collapse!important;font-size:8pt!important;width:100%!important}
.stuffbox table tr,.stuffbox table td{display:inline!important;padding:0!important}
.stuffbox table table table{white-space:nowrap!important;width:50%!important}
