@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(mail.live.com),
               domain(calendar.live.com){

/* ---------------------HEADER---------------------------*/

/*hide upper menu space*/
#PageBottomElt #EM2PX{
    display:none !important;
}
.Middle {
    top:7px !important;
}

/*move the menu down*/
#HeaderContainer *, #m_wh *{
background:transparent !important;
}

#c_header{
position: absolute !important;
bottom:0 !important;
height:2em !important;
}

/*hide everything than 'signout'*/
#c_header .c_hleft, #c_me .c_melnkspan, #c_profile, #c_wimm, #c_header ul .c_m t_hovl, #c_header .c_melnkspan{
display:none !important;
}

/*signout text*/
#c_signout{
position: fixed !important;
left:0.2em !important;
bottom:0.1em !important;
font-size:1.5em !important;
color:black !important;
font-weight:bold !important;
}

#c_signout:hover{
color:red !important;
}


/* ------------------------------------------------*/

/*hide bing logo*/
#c_search_psb_f{
    background-image:none !important;
}

/*collapse to the right(where the ads were)*/
#MainContent {
     margin:auto !important;
     right:0.5em !important;
}

/*hide left menu options: quickview and msn */
#quickViewList, #webimContactList{
      display:none !important;
}

/*hide ads*/
#SkyscraperContent, .Crm120Container, #RadAdSkyscraper, .Ad300x250, .c_ads_ad{
      display:none !important;
}

/*clean up some mess from the left-bottom in calendar mode*/
#c_content iframe{
      display:none !important;
}

/* ---------------------FOOTER---------------------------*/

/*hide footer*/
#FooterContainer, #m_wf, #uxp_ftr_control, #leftPanePrivacy {
      display:none !important;
}

}
