css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("365zw.com") {
#sitevote,#seting,#top_novels,
.chapter_nav.nav_border_bottom,
#site_notice, #chapter_pager span {
     display:none !important;
}

body[bgcolor] {
    background-color: #CCCCFF !important;
}

#container {
    background-color: #CCCCFF !important;
    border: 10px solid #339999 !important;
    -moz-border-radius: 0px 25px 0px 25px !important;
    padding: 20px !important;
}

.chapter_nav.nav_border_top {
    border: none !important;
    margin: 0px auto !important;
}

#text_area, #chapter_title {
    color: #222 !important;
    margin: 15px auto !important;
    font-size: 18px !important;
}

.chapter_nav.nav_border_top,
.chapter_nav.nav_border_top a,
.chapter_nav.nav_border_top a font,
#chapter_pager a{
    background: -moz-linear-gradient(center top, #339999 1px, #338899 10%, #337766) !important;
    color: #d8d8d8 !important;
    padding: 5px !important;
    -moz-border-radius: 10px !important;
}

.chapter_nav.nav_border_top a:hover,
.chapter_nav.nav_border_top a font:hover,
#chapter_pager a:hover {
    background: -moz-linear-gradient(center top, #009900 1px, #008800 10%, #007733) !important;
}
#chapter_title {
    border-bottom: 1px solid #339999 !important;
    text-align: left !important;
    width: 850px !important;
    padding-bottom: 10px !important;
}

#chapter_title h1 {
    font-weight: normal !important;
}

#chapter_pager {
    margin: 30px 0px 0px 0px !important;
}
}
