@namespace url(http://www.w3.org/1999/xhtml); /* for opera users: http://pastebin.ubuntu.com/482483 */
@-moz-document domain("pastebin.ubuntu.com"),domain("paste.ubuntu.com") {
body {margin: 0 !important; background: white !important;}
#contentColumn {width: auto !important; padding: 0 !important; -moz-box-shadow: none !important; -moz-border-radius: 0 !important;}
#contentColumn .inside,#contentColumn {top: auto !important; margin: 0 !important;}
div.paste,div.paste div {background: #f5ebd7 none !important; -moz-border-radius: 0 !important; -moz-box-shadow: none !important; margin: 0 !important;}
.inside>.paste {border: 1px #6d4c07 solid !important;}
h1,#title {color: #6d4c07 !important;  font-size: medium !important; font-size: 18px !important; text-align: left !important; padding: 0.4em 0 0 2px !important; text-shadow: 4px 3px 3px rgba(0,0,0,0.4) !important;}
#title {margin: 0 !important; background: none transparent !important; height: auto !important; width: auto !important;}
label,.pturl,th,td {color: black !important;}
#pasteform table {margin: 0 !important;}
textarea {font-size: 100% !important;}}
