@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rapidshare.com") {
#wrapper
{
position:absolute !important;
top: 150px !important;
left: 60px !important;
border: 0px !important;
}
#premiumtable,td
{
border:0px !important;
}
*:first-child + * > *[align="center"], 
*:first-child + TD[align="center"],
TR:first-child > *[align="center"], 
TR:first-child + * + * > *, 
TR:first-child + * + * + * > *,
TR:first-child + * + * + * + * > *,
TR:first-child + * + * + * + * + * > *,
TR:first-child + * + * + * + * + * + * > *,
TR:first-child + * + * + * + * + * + * + * > *,
TR:first-child + * + * + * + * + * + * + * + * > *,
TR:first-child + * + * + * + * + * + * + * + * + * > *
{
display:  none !important;
}
IFRAME, TD:first-child ,FONT > B, A[href="http://rapidshare.com"], DIV[id=footer], P:first-child > FONT[color="red"], P[align=justify], TABLE[id=premiumtable2], DIV[id=header], UL[id=mainmenu], H2, INPUT[value=PREMIUM], DIV[id=terminator], FORM[enctype="multipart/form-data"], A[href^="http://games.rapidshare.com"] 
{
display:  none !important;
}
#content
{
position: absolute !important;
top: -100px !important;
left: 130px !important;
}
h3
{
position: relative !important;
top: -25px !important;
color: white !important;
}
h3 img
{
position: relative !important;
left: 15px !important
}
body
{
background: none !important;
}
}
