css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.lotro.com") {

.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored {
border-right-color: rgb(0,0,0) !important;
border-left-color: rgb(0,0,0) !important;
border-top-color: transparent !important;
border-bottom-color: rgb(0,0,0) !important;
}

.postbit, .postbitim, .postcontainer, .signature, .userinfo {
border-right-color: rgb(50,50,50) !important;
border-left-color: rgb(50,50,50) !important;
border-top-color: transparent !important;
border-bottom-color: rgb(50,50,50) !important;
}

.rating0.sticky {
border-right-color: rgb(75,75,75) !important;
border-left-color: rgb(75,75,75) !important;
border-top-color: transparent !important;
border-bottom-color: rgb(75,75,75) !important;
}

.threadbit .threadstats, .author {
font-size: 9px !important;
}

}
