css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("threadcontent.next.ecollege.com") {

/* increases the font size and family on almost all messages */
.threadborder td,
.threadborder td p,
.threadborder td p font,
.threadborder td font,
.threadborder td li, 
.threadborder td html {
font-size: 0.9em !important;
font-family: verdana, "trebuchet ms", sans-serif;
}

/*colors unread message links dark red */
td.unread a:link {
color: #ac0000 !important;
}

/* colors the bar with unread message links light gray */
td.unread {
background-color: #efefef;
}

}
