css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://aigamedev.com/"), url-prefix("http://forums.aigamedev.com/") {

   .pagewidth {
      margin-left:   0px      !important;
      margin-right:  0px      !important;
      padding-left:  10px     !important;
      padding-right: 10px     !important;
      min-width:     1023px;
      width:         auto     !important;
   }

   .rightcolumn {
      margin:        20px 0px 20px 20px    !important;
      padding:       0 10px 0 20px         !important;
      width:         303px;
      height:        auto     !important;
   }

   .maincolumn {
      float:         none     !important;
      margin-left:   30px     !important;
      margin-right:  30px     !important;
      padding-left:  30px     !important;
      padding-right: 30px     !important;
      width:         auto     !important;
   }

   .maincolumn .latest_article {
      background:url("http://files.aigamedev.com/t/latest_article_bg.jpg") repeat-x scroll top transparent    !important;
   }

   #wrapper {
      padding:       20px 0   !important;
   }

   .page {
      margin:        0 0      !important;
   }

   .footer .latest_articles, .footer .newest_threads, .footer .newest_members {
      width: 560px            !important;
   }

   .footer .mangaright {
      left:          0px      !important;
   }

   .footermenu {
      padding:       30px 10px 20px 145px  !important;
   }
}
