css:
.tabbrowser-tab, .tabs-newtab-button {
border:2px solid transparent !important;
-moz-border-radius: 25px !important;
margin: 0px 10px !important;
min-width: 36px !important;
max-width: 36px !important;
padding:4px !important;
background-color:transparent !important;
}


.tabbrowser-tab:hover, .tabs-newtab-button:hover {
border-color: #fff !important;
background-color:#fff !important;
}


.tabbrowser-tab[selected="true"] {
border-color: #aaa !important;
background-color:#fff !important;
}


.tabbrowser-tab:not([visited]) {
border-bottom-color: #f55 !important;
}


.tabbrowser-tab .tab-icon-image {opacity:0.4!important}
.tabbrowser-tab:hover .tab-icon-image {opacity:0.85!important}
.tabbrowser-tab[selected="true"] .tab-icon-image {opacity:1!important}

.tabbrowser-tabs tab .tab-text { display: none; padding:0px !important; margin:0px !important;}
.tabbrowser-tabs tab .tab-icon { padding:0px !important; margin:0px !important;}
.tabbrowser-tabs tab .tab-icon-image {  min-width:26px;  min-height:26px; padding:0px !important; margin:0px !important;}
