css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {

/* Kiavash + gmail */
/* twitter.com/kiavash */
/* Twitters Right to left style */
    
    .tweet-text-rtl, .ProfileTweet .ProfileTweet-text, .tweet-body, .tweet-content, .rn-1k78y06 {
        font-family: Sahel, Vazir, dejavu sans, tahoma !important;
        font-size: 16px;
        line-height: 23px
    }

    /*.tweet-content {
        font-family: Shabnam, dejavu sans, tahoma !important;
        font-size: 12pt;
        line-height: 20px;
    }*/
    
    .tweet-body, .js-reply-tweetbox {
        text-align: right;
        direction: rtl;
    }
    
    .ProfileTweet--high .ProfileTweet-text {
        font-family: Sahel, nazli, b yekan, dejavu sans, tahoma !important;
        font-size: 21pt;
    }

    .tweet-form .tweet-box, #tweet-box-template, .js-compose-text, .js-reply-tweetbox  {
        font-family: Sahel, nazli, b yekan;
        font-size: 28pt !important;
        min-height: 180px !important;
        line-height: 40px !important;
    }

    .tweet-form .tweet-content #tweet-box-dm-conversation {
        font-family: Shabnam !important;
        font-size: 14pt !important;
        min-height: 125px !important;
        height: 150px !important;
        line-height: 40px !important;
    }
    #dm_dialog_conversation, .twttr-dialog-inside  {
        min-height: 625px !important;
        height: 650px !important;
    }
    .DMConversation-composer {
        top: 350px !important;
    }
    .inline-reply-tweetbox .tweet-box {
        height: 75px !important;
    }
    .tweet-actions-sidebar {
        text-align: right !important;
    } 
    
    #tweet-box-dm-new-conversation, #tweet-box-dm-conversation {
        height: 70px !important;
        font-size: 10pt !important;
        font-family: dejavu sans, tahoma;
        line-height: 15px !important;
    }
    
    .Grid-cell {
       /* width: 800px; */
    }
    
/*    .ProfileTweet-actionList {
        background: red;
        text-align: right !important;
        right: 0px;
    }  */
}
