css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(about:neterror), url-prefix(about:) { 

                          body { font-size: 11pt !important; color: #2f2f2f !important;} }

@-moz-document url-prefix(about:blank), url-prefix(about:Mozilla), url-prefix(view-source:) { 

                          * { font-size-adjust: .56 !important; background-color: #eee !important;} }

@-moz-document url-prefix(about:buildconfig), url-prefix(about:cache), url-prefix(about:licence) {

                          body { font-size-adjust: .56 !important; color: #2f2f2f !important;} }

@-moz-document url-prefix(about:config), url-prefix(about:credits), url-prefix(about:plugins) {

                          * { font-size-adjust: .69 !important; color: #3f3f3f !important;} }
