css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("watchanimeon.com") {
  /**
   * General restyling (layout)
   */
  #videopage > .clr:last-child,
x #videopage > .more-info > div,
  #videopage > :not(.adv-300) + .adv-300,
  #wrapper > div.clr,
  #wrapper > div.btm-contentm,
  #wrapper > div[class^="btm-advt"],
  #wrapper > .top-banner,
  iframe[name="frame5"] {
    display:  none;
  }
  #wrapper > a[title="logo"] {
    overflow: hidden;
    height:   50px;
    float:    left;
  }
  #wrapper > a[title="logo"] > img {  /* H:152px */
    margin-top: -102px  !important;
  }
  #wrapper > div.btm-sidebar {
    margin-right: -31px;
    width:         215px  !important;
    float:         right  !important;
  }
  #wrapper > #footer {
    clear: both;
  }

  /**
   * Extra styling for the episodes listing
   */
  #wrapper > div.btm-sidebar > .postlist > li > span {
    display:  none  !important;
  }
  #wrapper > div.btm-sidebar > .postlist > li > .sub + a:visited {
    color:  #999  !important;
  }
  #wrapper > div.btm-sidebar > .postlist > li > .raw + a {
    text-decoration: line-through  !important;
  }
  #wrapper > div.btm-sidebar > .postlist > li > a:focus,
  #wrapper > div.btm-sidebar > .postlist > li > a:hover {
    text-decoration: none  !important;
  }
}
