css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neect.ieeta.pt") {

div#search-box {
   margin-top: 14px !important;
}

div.pagination {
   color: blue !important;
   font-size: 10pt !important;
   font-weight: bold !important;
}

form#viewtopic > fieldset.display-options > a {
   color: black !important;
   font-size: 14pt !important;
   font-weight: bold !important;
}

div#footer {
   padding: 0px 0 10px ! important;
   font-size: small !important;
}

div#page-footer > div.copyright, 
div.back2top {
   display: none !important;
}

div#page-footer {
   padding-bottom: 15px !important;
}

dl.postprofile > dt > a {
   color: black !important;
   font-size: 11pt !important;
   font-weight: bold !important;
}

p.author > strong > a{
   color: black !important;
   font-size: 9pt !important;
   font-weight: bold !important;
   text-decoration: underline !important;
}

dl.postprofile {
   min-height: 70px !important;
   margin: 0 0 0 !important;
}

div#site-description > a, 
div#site-description > p {
   display: none !important;
}

div#site-description > h1 {
   margin-top: 9px !important;
   margin-left: 12px !important;
   color: #000000 !important;
}

div.headerbar {
   height: 50px !important;
}

div#search-box {
   margin-top: 0px !important;
}

div.headerbar > div.inner > span.corners-bottom {
   position: relative !important;
   top: -8px;
}

form#search > fieldset > a {
   position: relative !important;
   top: -5px;
}

div#page-body > ul.linklist > li:first-child > a:first-child + a + a + a::after {
   content: " (antigo \'Recente\')";
   font-weight: bold !important;
}

.bg1 {
   background-color: #FFFFFF !important;
}

.bg2 {
   background-color: #F5F5F5 !important;
}

.post {
   border-width: thin !important;
   border-color: grey !important;
   border-style: solid;
}

.panel, 
.navbar {
   background-color: lightgrey !important;
}

a:link {
   color: #222222 !important;
}

a:active {
   color: #333333 !important;
}

a:visited {
   color: #555555 !important;
}

div.headerbar, 
div.forabg {
   background: #999 !important; /* for non-css3 browsers */
   filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#cccccc', endColorstr='#FFFFFF') !important; /* for IE */
   background: -webkit-gradient(linear, left top, left bottom, from(#ccc), to(#fff)) !important; /* for webkit browsers */
   background: -moz-linear-gradient(top,  #ccc,  #fff) !important; /* for firefox 3.6+ */
}


span.corners-top, 
span.corners-bottom,
span.corners-top > span, 
span.corners-bottom > span {
   background-image: none !important;
}

}
