css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://browser/content/browser.xul){
/*Url-bar style*/

#PopupAutoCompleteRichResult .autocomplete-richlistitem {
    -moz-box-orient: horizontal !important;
    background-color: #FEFFFF !important;
    border-top-width: 0px !important;
    height: 24px !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem[current='true'] {
    background-color: #3293F3 !important;
}

#PopupAutoCompleteRichResult .autocomplete-richlistitem[selected] {
  background: #6CCAEC !important;
  box-shadow: none !important;
}

.ac-url {
    padding-left: 5px !important;
    border-left: 1px dotted #2b2b2b !important;
}

.ac-title {
    width: 450px !important;
}

.ac-emphasize-text {
    text-decoration: none !important;
}

.ac-action-icon,
.ac-type-icon,
.ac-ellipsis-after.ac-comment,
.ac-extra .ac-normal-text.ac-comment,
.autocomplete-history-dropmarker,
.ac-result-type-tag,
#star-button /*ä¹¦ç­¾å›¾æ ‡*/ {
    display: none !important;
}

.ac-url .ac-emphasize-text {
    font-weight: normal !important;
}

.ac-comment
{
    font-size: 100% !important;
    color: #2b2b2b !important;
    font-family: 'Mircosoft YaHei' !important;
}

.ac-comment[selected="true"]
{
    color: #feffff !important;
}

.ac-url-text {
    color: #798199 !important;
    font-style: oblique !important;
}
}
