css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sevas.ru") {

body { 
font-family: "Dejavu Sans", Helvetica, Verdana, Arial, sans-serif !important;
font-size:13px !important;
line-height:1.3em;
background:#555;

}

td#left_corn,#head_login,h1 {display:none;}
h1+div+table {
margin:50px 0 20px 0;
}


#right_corn, #right_corn td {

background:#444 !important;
color:#DDD !important;
/*text-shadow: 0px 0px 1px #666;*/
line-height:1.5em;
}

table td {
-moz-border-radius:9px;
-webkit-border-radius:9px;
}

#right_corn table td:first-child {
 width:30% !important;
}

a:link, a:visited, a:active {
color:#888 !important;
font-weight:bold;
text-decoration:none !important;
}

a:hover {
text-shadow: 0px 0px 2px #666;
color:#aaa !important;
}

h1+div {
top:10px !important;
}

#left_corn+td {
display:none;
}

#requested span {
font-size:12px !important;
}

#chat table td:first-child {
 width:2em !important;
}

}
