css:
/* custom insomnia.gr style by random 
*/
@namespace url(http://www.w3.org/1999/xhtml);

/* Common rules
------------- */

@-moz-document url(http://www.insomnia.gr/), url-prefix(http://www.insomnia.gr/vb3), url-prefix(http://www.insomnia.gr/index.php?option ){

body {
background: #00303b url(http://www.insomnia.gr/templates/insomnia/images/logo.jpg)  no-repeat top left !important;
}

#wraptopmenu a {color: #E4EEEC !important;}
#wraptopmenu a:hover {color: #FFFF00 !important;}

.tfoot
{ background: #b3e1ee none repeat-y top left !important;
 color: black !important;
}
.tfoot a:link, .tfoot_alink {	color: black !important; }
.tfoot a:visited, .tfoot_avisited {	color: black !important; }
.tfoot a:hover, .tfoot a:active, .tfoot_ahover {	color: mediumblue !important; }

.navbar{
font-size: 11pt !important;
font-style: normal !important;
font-weight: lighter !important;
color: #094D5E !important;
}
.greenbarfill{
background-color: #00A0C6 !important;
color: yellow !important;
font-size: 12px !important;
margin-left:1px !important;
margin-right:1px !important;
}
.greenbartext a{
font-size: 12px !important;
color: yellow !important;
}
.greenbarleft{
BACKGROUND-COLOR: #00A0C6 !important;
}
.greenbarright{
BACKGROUND-COLOR: #00A0C6 !important;
}
.smallfontdown, .smallfontdown ul, .smallfontdown li { color: black !important; }

}

/* Forum pages
------------- */

@-moz-document url-prefix(http://www.insomnia.gr/vb3), url-prefix(http://www.insomnia.gr/index.php?option ) {

body {
background: #E4EEEC none no-repeat top left !important;
}

#header {display: none !important}
#left {display: none !important}
#right {display: none !important}
#content {margin-left:0px !important;}

}
