css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
/* http://m.bolhasani.com/ */
/* Mehran Bolhasani (mehran751@gmail.com) */
/* I love Clean Design*/

url-prefix(http://twitter.com/),
url-prefix(https://twitter.com/){

body {
font-family: tahoma !important;
}

.stream-item {
text-align: right;
direction: rtl;
}

.tweet-image {
float: right !important;
}

.tweet-content {
margin-right: 58px !important;
margin-left: 0 !important;
}

.tweet-user-name {
margin-right: 10px !important;
font-size: 11px !important;
}

.tweet-text {
font-family: tahoma !important;
font-size: 11px !important;
padding: 2px 0 !important;
}

.protected-icon-timeline {
margin:0 0 0 5px !important;
}

._timestamp {
float: left;
direction: ltr;
}

.tweet-actions {
direction: ltr;
}

.extra-icons {
position: absolute;
right: 430px !important;
}

.tweet-box textarea {
font-family: tahoma !important;
font-size: 11px !important;
text-align: right;
direction: rtl;
}
}
