css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
 url-prefix("chrome://"),
 url-prefix("file:///"),
 url-prefix("ftp://"),
 url-prefix("http://"),
 url-prefix("https://")
{

 pre
 {
   font-family: 'lucida console', monospace !important;
   line-height: 1.5em !important;
 }

html >
 body:not([bgcolor]) >
 pre:only-child
 {
   background: #111 !important;
   color: #bbb !important;
   margin: 0 !important;
   overflow: auto !important;
   padding: 5px !important;
   white-space: pre-wrap !important;

   position: fixed !important;
   bottom: 0 !important;
   left: 0 !important;
   right: 0 !important;
   top: 0 !important;
 }

}

@-moz-document 
 url-prefix("view-source:")
{

html 
 {
   background: #111 !important;
   margin: 0 !important;
   padding: 0 !important;
 }

html >
 #viewsource
 {
   bottom: 0 !important;
   color: #999 !important;
   left: 0 !important;
   margin: 0 !important;
   min-height: 100% !important;
   padding: 0 !important;
   right: 0 !important;
   top: 0 !important;
 }

html >
 #viewsource >
 pre
 {
   padding: 5px !important;
   white-space: pre-wrap !important;
 }

.attribute-name
 {
   color: #587e82 !important;
   font-weight: normal !important;
 }
.attribute-value
 {
   color: #3f5f47 !important;
 }
.end-tag,
.start-tag
 {
   color: #827658 !important;
 }

}
