css:
@-moz-document domain("technologijos.lt"){
   body>center>table,  body>center>div>table{
      width: 100% !important;
   }

   td.virsutine_juosta_td{
      display: none;
   }


   table[width="1010px"]>*>tr>td:nth-of-type(2) {
      display: none;
   }

   table[width="1010px"]>*>tr>td:nth-of-type(3), td#portal-column-one-2, td#portal-column-two-2 {
      background-color:lightgray !important;
      max-width: 5px;
      width: 5px;
   }

   table[width="1010px"]>*>tr>td:nth-of-type(3)>*, td#portal-column-one-2>*, td#portal-column-two-2>* {
      display:none;
      visibility: hidden;
      margin: 5px;
   }

   table[width="1010px"]>*>tr>td:nth-of-type(3):hover, td#portal-column-one-2:hover, td#portal-column-two-2:hover {
      width: auto;
      max-width:300px;
   }


   table[width="1010px"]>*>tr>td:nth-of-type(3):hover>*, td#portal-column-one-2:hover>*, td#portal-column-two-2:hover>* {
      display:block;
      visibility: visible;
   }

    td.dienos_naujiena_aprasymas_td div.first_thumb.left img {
       width: 15em !important;
    }
    td.dienos_naujiena_aprasymas_td div.first_thumb.right img {
       width: 10em !important;
    }

    table.info_table div.lygiuoti_centras img {
      max-width: 10em !important;
    }
}
