css:
@-moz-document domain(facebook.com), domain(www.facebook.com) {
.like_link .default_message{ display:block!important;width:20px!important;height:15px!important;text-indent:-9000px;}
.like_link[name="like"] .default_message{ background: transparent url(http://itigoppo.web.fc2.com/facebook/fav-add.gif)  no-repeat 0px -5px!important;}
.like_link[name="unlike"] .default_message{ background: transparent url(http://itigoppo.web.fc2.com/facebook/fav-del.gif)  no-repeat 0px -5px!important;}
.cmnt_like_link .default_message{ display:block!important;width:20px!important;height:15px!important;text-indent:-9000px;}
.cmnt_like_link[name*="like"] .default_message{ background: transparent url(http://itigoppo.web.fc2.com/facebook/fav-add.gif)  no-repeat 0px -5px!important;}
.cmnt_like_link[name*="unlike"] .default_message{ background: transparent url(http://itigoppo.web.fc2.com/facebook/fav-del.gif)  no-repeat 0px -5px!important;}
}
