@-moz-document domain("deviantart.com") {

/* Userpage & message center poll */

div.ctube div.cfill {

    background:url(http://fc01.deviantart.net/fs70/f/2010/294/7/c/poll_bar_by_litttle_princess-d3173t1.png)!important;

}

div.ctube div.cfill span.sub10 {

color:#FFFFFF!important;

}



/* Footer poll */

.pollpole,

.poll-bar {

	background-color:#faf6f7!important;

	border-color:#ff008d!important;

	color:#230118!important;

}

