css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("godville.net") {

html, body, .rounded_field {
background-color: #333 !important;
color: #bbb !important;
font-size: 87% !important;
}

legend {
background: #888 !important;
color: #FFF !important;
}

legend a, a, #login label, #login h2, .hero h2 {
color: #FFF !important;
}

.text_content, .article, .news, .crumbs,
th, fieldset, pre, blockquote, 
td.inv, tr.rowA, tr.rowB, .hero, .txt input, .h_capt, .post_text,
#left_block, #central_block, #right_block, #content, #login, 
#login .textfield {
background: #333 !important;
}

th, td {
color: #bbb !important;
}

.pr_bg {
background-color: #F00 !important;
height: 3px !important;
}

.pr_bar {
background-color: #333 !important;
height: 3px !important;
}

li, .equip_content, .field_content, .h_capt,
#login .textfield {
color: #fb0 !important;
}

#logo {
visibility: collapse !important;
}

}
