css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mlive.com") {

div[id^=bottom-text-ads],
div[id^=right]
{display: none !important;}

#left { width: 100% !important; }
#content-right { width: 800px !important; }
.blog-post p, .blog-post, .entry-body, .blog-post blockquote { text-align: justify !important; width: 100% !important; font-family: verdana !important; font-size: 12px !important; }
.individual { font-family: verdana !important; }
body, td { font-family: verdana !important; }

}
