css:
/*
Custom Skin     : Myspace User Home Page - Dark
Skin Author	: MySpace Compilation (MSC)
Author Website	: http://myspace-compilation.com
Skin Applied To : http://myspace.com/home

*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.myspace.com/home"), url-prefix("http://www.myspace.com/home#pm_cmp=uhp_nav_home"), url-prefix("http://secure.myspace.com/home"), url-prefix("http://home.myspace.com/home#pm_cmp=uhp_nav_home")

{

body {background-image: url(http://www.myspace-compilation.com/grad.php?type=top&width=10&height=600&start_color=cccccc&end_color=ffffff) !important; background-repeat:repeat-x !important; background-attachment:fixed !important; margin-top:-15px !important;}

div#searchContainer_Header.global {visibility:hidden;}

div#gblmslogo.clearfix {margin-top:-10px !important;}

div#gbltopnav.clearfix ul {background-color:#aaa !important; padding-left:25px !important; margin-top:-5px !important;}

div#gbltopnav.clearfix ul li {background-color:#aaa !important;}

div#gbltopnav.clearfix ul li ul.gblsubMenu li a {margin-left:-25px !important;}

div#gbltopnav.clearfix ul li ul.gblsubMenu li:hover a {color:#fff !important; margin-left:-25px !important;}

div#gbltopnav.clearfix ul li a {border:0px !important;}

div.contentMid2 {background-image: url(http://www.myspace-compilation.com/grad.php?type=top&width=10&height=800&start_color=aaaaaa&end_color=F0F8FF) !important; background-repeat:repeat-x !important; background-attachment:fixed !important; margin-top:-5px !important; border-top:3px solid #0077DB !important;}

div.superShareContainer div.textarea {background-color:#EDEDED !important; border:1px solid #0077DB !important;}

div.superShareContainer textarea {color:#000 !important;}

div.superPostModule span.statusArrow {display:none !important;}

div.streamContainer li {background-color:#DBDBDB !important;}

div.streamContainer li div.activityBody {background-color:#DBDBDB !important;}

div.uhpAlertsModule div.moduleBody a {background-color:#ccc !important; color:#000 !important; border:1px solid #ccc !important;}

div.uhpAlertsModule div.moduleBody ul li {background-color:#ccc !important; color:white !important;}

div.recommendationsModule ul li {background-color:transparent !important;}

div#footer {background-color:#EDEDED !important; padding:0px !important;}
 
}
