css:
body {background-color:#fafafa !important;}
#tabs li a.active {background-color:#721 !important;color:white !important;}
#workspace .button {background-color:#444 !important; border:1px solid black !important;position:relative;}
#workspace .button:hover {padding:6px 10px !important;}
#workspace .button:active {top:1px;}
#workspace #workspace_header {background-color:#721 !important;color:white;border-bottom:1px solid #222;margin-bottom:0 !important;}
#workspace #workspace_header a {color:#eaeaea !important;text-decoration:none;}
#workspace #workspace_header .milestone_date {color:#cc3 !important;}
#workspace .todo_list {background-color:#fafafa;}
#workspace .todo_list .todo_list_name, #workspace .milestone_title {background-color:#333 !important;color:white !important;box-shadow:0px 0px 5px #333;-moz-box-shadow:0px 0px 5px #333;-webkit-box-shadow:0px 0px 5px #333;position:relative;}
#workspace .todo_list .todo_list_name a {color:#f0f0f0 !important;}
#workspace .todo_list .todo_item {background-color:#ffa !important;border:1px solid #dd8 !important;}
#workspace .todo_list .todo_item a:hover {color:#721 !important;}
#workspace .todo_list .todo_item.ui-sortable-placeholder {background-color:#fff !important;border:1px solid #ccc !important;box-shadow:none !important;-moz-box-shadow:none !important;-webkit-box-shadow:none !important;}
#workspace .todo_list .col_header_row {background-color:#dadada !important;border-bottom:1px solid #ccc;}
#workspace .todo_list .row {border-top:1px solid white;}
