css:
#launchbar, #Header {position:fixed !important;top:0 !important;width: 100% !important;}
#launchbar {z-index:100 !important;background-color: #17110D !important;}
#Header {z-index:99 !important;margin-top:20px;}
#Wrapper {margin-top:150px !important;}


#Header div.navigation, #Header div.navigation a:link, #Header div.navigation a:visited, h1 {color: #879796 !important;}
 #Header h1 {font-size:3em !important;letter-spacing: -3px !important;font-family: Alte Haas Grotesk, Helvetica Neue, Helvetica, Arial, sans-serif;}
  #Header h1 span {letter-spacing: -1px !important;}
#Header div.navigation a:hover {background-color:#B09880 ;color: #2B211F !important;}
#Header {background-color:#2B211F;}
#Tabs a:hover {background-color:#879796;color: white;}
a.current {background: #9B6F64 !important;}
