css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.seriesyonkis.com"), domain("www.peliculasyonkis.com") {


*
{
background-image:none!important;
}

.quadbar #bodyowner
{
width:700px!important;
}

iframe,
.quadbar #menucontainer, 
.textwidget, 
.blogbefore, 
.banner,
#contentcontainer p,
div.post>div>i,
div.post>div>strong,
div.post>div>br,
div.post>div>center,
div.post>div>h2,
div.post>div>h3,
div#contentcontainer div.post div h4,
#contentcontainer .blogafter
{
display:none!important;
}

iframe#peli,
div#bodyowner div#menucontainer div.middlemenugroup,
#contentcontainer h1 a
{
display:block!important;
margin:20px auto!important;
}

iframe#peli
{
margin:0 auto!important;
height:140px!important;
overflow:hidden!important;
}


.blogafter .middle
{
margin:0!important;
}

body.headerfixed.plainheader .named, 
#contentcontainer div.headertext, 
#commentform
{
display:none!important;
}

div#contentcontainer div.post div table
{
background-color:#2a2a2e;
margin:20px auto!important;
}
.post th, 
.post td 
{
border: 2px solid #303036!important;
}

#header 
{
width:643px!important;
margin:0 auto!important;
position:relative!important;
}

body
{
font-family:TitilliumText22L!important;
font-size:14px;
background-color:#303036!important;
padding:15px 0!important;
}

body.headerfixed.plainheader #bodyowner 
{
padding-top:0!important;
}

#formError,
iframe#peli h3
{
display:none!important;
}

table
{
width:100%!important;
}
}
