css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bmw.jugem.cc") {

/* Non-display part */
#snap_com_shot_main,
#snap_com_shot_main + a + br + table,
#snap_com_shot_main + a + br + table + hr,
#snap_com_shot_main + a + br + table + hr + table,
#snap_com_shot_main + a + br + table + hr + table + hr,
#snap_com_shot_main + a + br + table + hr + table + hr + table,
body > center,
body > br,
body > hr,
body > table > tbody > tr > td[width="220"],
body > table > tbody > tr > td[width="480"] > br,
body > table > tbody > tr > td[width="480"] > a,
body > table > tbody > tr > td[width="480"] > center,
.entry > table[width="440"],
.entry > table[width="470"] > tbody > tr > td > img,
.entry_state,
.entry_state + img,
a[name="comments"] + div.entry,
a[name="trackback"] + div.entry,
#gEntryAdContainer
{ display: none !important; }

/* Display part */
body,
body > table,
body > table > tbody > tr > td[width="480"],
.entry > table[width="470"]
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    margin: 0 0 0 !important;
}

td
{
    background: none !important;
}


}
