css:
@-moz-document url-prefix(http://allegro.pl/) {


#showCase, #showCaseSmall, #footer-ads, #rightBox, #logo img
{display: none;}

#contentRight div.contentBox
{margin-top: -5px;}

#buttonsGuide
{margin-top: 0px !important;}

#contentBoxTop
{height: 14px !important; background: none !important;}

#search
{padding-bottom: 15px !important;}

#contentRight div.contentBox  ul li a
{margin: 2px !important; display: block; padding: 2px;}

#contentBoxTop
{background: #7790B6 !important; -moz-border-radius: 10px 10px 10px 10px; padding: 5px 5px 15px 5px !important;}

#topMenu img
{width: 50px;}

#logo
{height: 50px;}

#outlineLeftRight
{margin-top: -10px !important; background-image: none !important; background-color: #F4F4F4 !important;}

#leftBox
{background: #F6F5F3 !important;}

.tab-headcolor1, .list-menu-descx
{background-color: #EAE8E3 !important; border-color: #AEAEAE !important;}

.list-menux
{border-color: #AEAEAE !important;}

.td-pager
{background: #F7F7F7; padding: 5px;}

}
