css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Make sure you change the colors in both sections together or you'll get confused! */

@-moz-document domain("tbpl.mozilla.org") {

  /* This colors the test results */
  span > a.testfailed { /* Normally orange */
    color: orange !important;
  }

  span > a.success { /* Normally green*/
    color: green !important;
  }

  span > a.busted { /* Normally red */
    color: red !important;
  }


  /* This colors the squares in the top corner of the page */
  div > a[class="machineResult testfailed"] { /* Normally orange */
    background-color: orange !important;
  }

  div > a[class="machineResult success"] { /* Normally green */
    background-color: green !important;
  }

  div > a[class="machineResult busted"] { /* Normally red*/
    background-color: red !important;
  }
}
