css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {


* { 
	font-family: "Trebuchet MS", Courier New !important;
}

a {
	text-decoration: none !important;
	color: #666 !important;
}

a:hover {
	border-bottom: 1px dashed #888 !important;
	color: #888 !important;
}

.news h2 a {
	color: #B4C24B !important;
	border: none !important;
}

.news h2 a:hover {
	color: #58BBEB !important;
	border: none !important;
}

h1 a {
	color: #E87830 !important;
	border: none !important;
}

h1 a:hover {
	color: #CC0000 !important;
	border: none !important; 
}

.news {
	border-top: 1px solid #ccc !important;
}

#ibmdw {
	display: none !important;
}

.news a {
	border-bottom: 1px dashed #666 !important;
}

.news .entry-userpic a {
	border: none !important;
}

.news .entry-userpic {
	display: none !important;
}

.news .entry-body {
	margin-left: 5px !important;
}

body {
	text-align: center !important;
}

body * {
	text-align: left !important;
	max-width: 1200px !important;
	margin: 0pt auto !important;
}

.newsblog {
	float: none !important;
}

.column {
	display: none !important;
}

.news {
	margin-top: 25px !important;
	padding-top: 20px !important;
	border-top: 1px dashed #ccc !important;
}

body table {
	display: none !important;
}
body * table {
	display: block !important;
}

body center table a {
	margin: 0px 35px !important;
}
.entry-body{
	color: #444;
	font-size:  12px !important;
}
.entry-body .msg {
	color: #222 !important;
	font-size: 16px !important;
	margin-bottom: 20px !important;
}

.entry-body .sign {
	font-size: 12px !important;
}
.entry-body .sign:before {
	content: 'ÐÐ²Ñ‚Ð¾Ñ€: ';
	color: #444;
}
.entry-body .nav {
	margin-top: 10px !important;
	font-size: 12px;
}
body>p, body>div>p, body>div>a{
	display: none !important;
}
body>p:before {
	display: block !important;
	content: 'inspired by simpla theme for wordpress';
}
.newsblog-in>.nav{
	margin-top: 20px !important;
	border: none !important;
	border-top:  1px dashed #ccc !important;
	color: #fff;
}

body>center:before {
	content: '<Linux.org.ru/>';
	display: block !important;
	color: #E87830 !important;
	border-bottom: 1px dashed #ccc;
	font-size: 30px;
	font-weight: bold;
	padding: 0px !important;
	margin: 0px !important;
}
body>center>table {
	margin-top: 20px !important;
}


}
