css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("techdirt.com") {
 /* Destroy Things */
  #pagefoot,
  #rightcolumn,
  #leftcolumn,
  .comments,
  .storysep
   {
     background: none !important;
   }
  #pagefoot,
  .comments,
  .storysep
   {
     border-bottom: none !important;
     border-left: none !important;
     border-right: none !important;
     border-top: none !important;
   }
  #goldpromo,
  .ad
   {
     display: none !important;
   }

 /* Global Style */
  html, body
   {
     background: #111 !important;
     color: #999 !important;
   }
  a:link,
  a:visited
   {
     background: none !important;
     border: none !important;
     color: #EAD6AC !important;
     text-decoration: underline !important;
   }
  a:active,
  a:hover
   {
     background: none !important;
     border: none !important;
     color: #EAD6AC !important;
     text-decoration: none !important;
   }
  h1
   {
     color: #ccc !important;
   }
  h2
   {
     color: #aaa !important;
   }
  h3, h4, h5, h6
   {
     color: #888 !important;
   }
  input, select, textarea 
   {
     color: #bbb !important;
     background: #222 !important;
     border: #666 1px solid !important;
     margin: 2px !important;
     padding: 1px !important;
   }

  #navigation a:link,
  #navigation a:visited
   {
     background: none !important;
     border: none !important;
     color: #eee !important;
     text-decoration: none !important;
   }
  #navigation .selected a:link,
  #navigation .selected a:visited
   {
     background: none !important;
     border: none !important;
     color: #000 !important;
     text-decoration: none !important;
   }
  #navigation a:active,
  #navigation a:hover
   {
     background: none !important;
     border: none !important;
     color: #ccc !important;
     text-decoration: none !important;

   }
  #navigation .selected
   {
     background: #aaaa8b !important;
     border-left: 1px solid #000 !important;
     border-right: 1px solid #000 !important;
     border-top: 1px solid #000 !important;
     padding-bottom: 4px !important;
   }
  #subnav a:link,
  #subnav a:visited
   {
     background: none !important;
     border: none !important;
     color: #222 !important;
     text-decoration: none !important;
   }
  #subnav a:active,
  #subnav a:hover
   {
     background: none !important;
     border: none !important;
     color: #666 !important;
     text-decoration: none !important;

   }
  #subnav
   {
    background: #aaaa8b !important;
   }

  li.comments
   {
     background: #111 !important;
     border: 1px dotted #333 !important;
   }
  .maincolumn_head
   {
     background: #333 !important;
     border-bottom: 1px solid #aaaa8b !important;
     border-left: 1px solid #aaaa8b !important;
     border-top: 1px solid #aaaa8b !important;
     color: #bbb !important;
   }
  .rightcolumn_head
   {
     background: #333 !important;
     border-bottom: 1px solid #aaaa8b !important;
     color: #bbb !important;
   }
  .rightcolumn_head:first-child
   {
     background: #333 !important;
     border-bottom: 1px solid #aaaa8b !important;
     border-right: 1px solid #aaaa8b !important;
     border-top: 1px solid #aaaa8b !important;
     color: #bbb !important;
   }

}
