css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("publib.boulder.ibm.com") {
body, div.conbody, table td, span, pre, div.experience,div.dochomefooter {
  background-color: #444   !important;
  color: #eee  !important;
}

div.conbody {
  max-width: 640px;
}

dd:hover, p:hover,li:hover, span:hover, pre:hover, blockquote:hover, div.dochomefooter:hover {
  background-color: #eee !important;
  color: #000 !important;
}

div.dochomefooter:hover a,
blockquote:hover a,
li:hover a,
p:hover a {
  color: #448 !important;
}

div.dochomefooter:hover a:hover,
blockquote:hover a:hover,
li:hover a:hover,
p:hover a:hover,
span:hover a:hover {
  color: #5a4 !important;
}

h1,h2,h3,h4,h5{
  color: #eee  !important;
}

h1.dochomeh1 {
  color: #000 !important;
}

a {
  color: #ffa  !important;
  text-decoration: none  !important;
}

body a:hover {
  color: #ffa !important;
  background-color: 77f !important;
}

div.note {
 padding: 5px;
 border: 1px solid #aaa;
 background: #888
}

body, p {
 font-family: Palatino, Century Gothic, Cambria, Georgia, Verdana, Arial !important;
 font-size: 12pt !important;
}

div#tree_root {
 font-size: 10pt !important;
}

div#idt * {
 color: #444 !important;
}

}
