css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("barter21.com") {

#siteWrap {
  width: 860px !important;
}

#sideColumn {
  width: 200px !important;
}

#mainColumn {
  width: 640px !important;
}

#siteWrap, #siteWrap p, #siteWrap div, #siteWrap td, #siteWrap li {
  background-color: #444 !important;
  color: #eee  !important;
  font-size: 12pt !important;
  font-family: Palatino, Century Gothic, Cambria, Georgia, Verdana, Arial !important;
}

#siteWrap a, #siteWrap p a, #siteWrap div a {
  color: #3aa !important;
  font-size: 10pt;
}

#siteWrap p:hover, #siteWrap tr:hover, td:hover {
  background-color: #eee !important;
  color: #000 !important;
}

#sideColumn .left_navigation ul li {
  border: none !important;
}

.user2 {
  color: #eee !important; 
  font-size: 11pt !important;
  font-weight: bold !important;
  padding-bottom: 8px !important;
  display: inline-block !important;
}

.user3 {
  color: #eee !important;
}

}
